    private void moviment_topMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_moviment_topMousePressed
        xMouse = evt.getX();
        yMouse = evt.getY();
    }//GEN-LAST:event_moviment_topMousePressed

    private void moviment_topMouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_moviment_topMouseDragged
        int x = evt.getXOnScreen();
        int y = evt.getYOnScreen();
        this.setLocation(x-xMouse, y-yMouse);
                
    }//GEN-LAST:event_moviment_topMouseDragged

